import React from 'react';
import ReactMarkdown from 'react-markdown';
import { Link } from 'react-router-dom';

const PolicyLayout = ({ markdownContent }) => {
  return (
    <div className="h-screen w-screen bg-white flex items-center justify-center relative overflow-hidden font-georgia">
      <div className="w-[98%] h-[95%] bg-gradient-to-br from-[#ebf4f5] to-[#b5c6e0] rounded-[30px] shadow-xl flex flex-col items-center justify-center relative overflow-y-auto">
        <div className="absolute top-4 left-4">
          <Link
            to="/"
            className="bg-black text-white px-4 py-2 rounded-full text-sm hover:bg-gray-800 transition-colors"
          >
            Back to Home
          </Link>
        </div>

        <div className="max-w-3xl mx-auto px-6 py-12">
          <div className="prose prose-slate">
            <ReactMarkdown>{markdownContent}</ReactMarkdown>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PolicyLayout;
